vowels = ["a", "e", "i", "o", "u"]

def checkforvowels(string):
    count = 0
    for character in string:
        for vowel in vowels:
            if character == vowel:
                count += 1
    print(count)

if __name__ == "__main__":
    checkforvowels("test")

